    </Route>
    <Route path="/orders">
    <Orders cart={cart}/>
    </Route>
      <Route exact path="/">
    <Login />
    </Route>
    </Switch>
      <header style={{textAlign:"center", backgroundColor:"lightblue", fontSize:"35px"}}>Jambo-Journeys App</header>
      <HotelList hotels = {hotels}/>
      <ParkList parks = {parks}/>
      <AddHotel addHotel = {addHotel}/>
      <AddPark addPark = {addPark}/>
    </div>
  );
}

export default App;
