import React, {useState} from "react";
import { useHistory } from "react-router-dom";

function Contacts(){
    const history = useHistory()

    // declare form variables
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [feedback, setFeedback] = useState("")

    function handleSubmit(e){
        e.preventDefault()

        // object that will hold data
        const newMessage = {
            name:name,
            email:email,
            feedback:feedback,
        }

        // fetch review data
        fetch("/reviews", {
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify(newMessage)
        })
        .then((r) => r.json())
        .then((data) => console.log(data))
        setInputClear()

        history.push("/")
    }
    function setInputClear(){
        setName("")
        setEmail("")
        setFeedback("")
    }
    return(
        <div className="fm1">
            <form className="reviews" onSubmit={handleSubmit}>
                <h2 style={{textAlign:"center"}}>Contacts Page</h2>
                <p style={{textAlign:"center"}}>We would love to hear from you</p>

                <label for="name">Name</label>
                <input type="text" style={{width:""}} id="name" value={name} placeholder="daniel" onChange={e => setName(e.target.value)}/>

                <label for="name">Email</label>
                <input type="text" id="email" value={email} placeholder="your-email@13.com" onChange={e => setEmail(e.target.value)}/>

                <label for="name">Feedback</label>
                <input type="text" id="feeds" value={feedback} placeholder="Your reviews/suggestions" onChange={e => setFeedback(e.target.value)}/>

                <button style={{backgroundColor:"green", cursor:"pointer",padding: "10px",border: "none"}} className="submit-message" type="submit">Submit</button>


            </form>
        </div>
    )
}

export default Contacts
