import React,{useState} from "react";

function AddHotel({hotel, setHotel}){
    const [formData, setFormData] = useState({
        name:"",
        image_url:"",
        description:"",
        location:"",
        prices:"",
    })

    const handleSubmit = (e) => {
        e.preventDefault()

        const newHotel = {
            name:formData.name,
            image_url:formData.image_url,
            description:formData.description,
            location:formData.location,
            prices:formData.prices,
        };

        // post request
        fetch("", {
            method:"POST",
            headers: {
                "Content-Type":"application/json",
            },
            body: JSON.stringify(newHotel)
        })
        .then((r) => r.json())
        .then((data) => {
            setHotel([...hotel,data]);

            // reset the form data to empty strings after adding hotel
            setFormData({
                name:"",
            })
        })
    }

    return(
        <div className="addhotel">
            <h2 style={{textAlign:"center", color:"lightblue"}}>ADD Hotel</h2>
            <p style={{textAlign:"center", backgroundColor:"grey"}}>To add a Hotel kindly fill up the form below</p><br></br><br></br><br></br>

            <form className="addhotel-form" onSubmit={handleSubmit}>
                <div className="form1">
                    <label htmlFor="name">Enter Hotel Name: </label><input type="text" placeholder="Ole-Sereni" id="name" value={hotel.name} onChange={handleChange}/><br></br>
                    <label htmlFor="description">Enter Description: </label><input type="text" placeholder="Review" id="desc" value={hotel.description} onChange={handleChange}/><br></br>
                    <label htmlFor="location">Enter Location: </label><input type="text" placeholder="where it's located" id="loc" value={hotel.location} onChange={handleChange}/><br></br>
                    <label htmlFor="prices">Enter Price: </label><input type="text" placeholder="price for booking" id="price" value={hotel.prices} onChange={handleChange}/><br></br>
                    <label htmlFor="image_url">Enter image URL: </label><input type="text" placeholder="jpeg/42" id="imag" value={hotel.image_url} onChange={handleChange}/><br></br><br></br>

                    <button className="btn2" type = "submit">Add hotel</button>


                </div>
            </form>
        </div>
    )
}

export default AddHotel
